DROP TABLE IF EXISTS `p_work_report`;
CREATE TABLE `p_work_report` (
  `id` bigint NOT NULL COMMENT '主键;主键',
  `context` varchar(32) COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '内容;内容',
  `create_date` datetime DEFAULT NULL COMMENT '创建时间;创建时间',
  `create_by` varchar(32) COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '创建人;创建人',
  `update_date` datetime DEFAULT NULL COMMENT '更新时间;更新时间',
  `del_flag` int DEFAULT NULL COMMENT '0-有效，1-无效;0-有效，1-无效',
  `file_url` varchar(255) COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '附件地址;附件地址',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci COMMENT='工作记录表';

